\draw[red] (1,1) node[below right]{$B(x)$};
\YExcoord{-3}{a}
\YExcoord{3}{b}
\YExcoord{-1}{a'}
\YExcoord{1}{b'}

%interval [a,b] has midpoint M, B(M)=B, T(m)=B; center of mass is C
\foreach \x in {0,...,3}{
	\MULTIPLY{\x}{0.5}{\dx}
	\ADD{-3}{\dx}{\a}
	\ADD{\a}{0.5}{\b}
	\ADD{\a}{\b}{\MM}
	\DIVIDE{\MM}{2}{\M}
	\ADD{5}{\M}{\TT}
	\MULTIPLY{\TT}{.5}{\T}
	\ADD{1}{\M}{\BB}
	\MULTIPLY{\BB}{-.5}{\B}
	\draw (\a,\B) rectangle (\b,\T);
